// Create item data (each item's fields separated by attribute marks)
CPSTR * item1 = _CP_mkstr("6/10/2020\xfe6/9/2020\xfe150\xfe2500");
CPSTR * item2 = _CP_mkstr("9/03/2021\xfe9/06/2021\xfe32000\xfe30000");

// Combine items with segment marks
CPSTR * items = _CP_str_null;
_CP_cat(&items, item1, _CP_mkstrl("\xff", 1));
_CP_cat(&items, items, item2);

// Write the fields
_CP_writefields(_CP_READ, &returnset, &statusset, dictfd, datafd, 
               itemidlist, items, &lockedresult, fieldnames);
